	IF OBJECT_ID('tempdb..#data','U') IS NOT NULL DROP TABLE #data; --SELECT * FROM #data
	CREATE TABLE #data (
		ID				int				NOT NULL PRIMARY KEY CLUSTERED,
		RowID			int				NOT NULL,
		Val				char(1)			NOT NULL,
		BracketLevel	int				NOT NULL,
		MatchOpenSide	char(1)				NULL,
		MatchCloseSide	char(1)				NULL,
		IsCorrupted		bit				NOT NULL DEFAULT(0),
	);

	WITH cte_Data AS (
		SELECT ID = ROW_NUMBER() OVER (ORDER BY r.ID, x.ColID)
			, RowID = r.ID, x.Val
			, Side = IIF(x.Val IN ('(','[','{','<'), 'o', 'c')
		FROM #rawdata r
			CROSS APPLY (
				SELECT ColID = ROW_NUMBER() OVER (ORDER BY 1/0)
					, Val = SUBSTRING(r.Val, ROW_NUMBER() OVER (ORDER BY 1/0), 1)
				FROM STRING_SPLIT(REPLICATE(',', LEN(r.Val)-1), ',') x
			) x	
	)
	INSERT INTO #data (ID, RowID, Val, BracketLevel, MatchOpenSide, MatchCloseSide)
	SELECT x.ID, x.RowID, x.Val, x.BracketLevel
		, MatchOpenSide  = IIF(x.Side = 'c', LAG(x.Val)  OVER (PARTITION BY x.RowID, x.BracketLevel ORDER BY x.ID), NULL)
		, MatchCloseSide = IIF(x.Side = 'o', LEAD(x.Val) OVER (PARTITION BY x.RowID, x.BracketLevel ORDER BY x.ID), NULL)
	FROM (
		SELECT d.ID, d.RowID, d.Val, d.Side
			, BracketLevel = SUM(IIF(d.Side = 'o', 1, -1)) OVER (PARTITION BY d.RowID ORDER BY d.ID) + IIF(d.Side = 'c', 1, 0)
		FROM cte_Data d
	) x

	IF OBJECT_ID('tempdb..#map','U') IS NOT NULL DROP TABLE #map; --SELECT * FROM #map
	CREATE TABLE #map (
		OpenChar		char(1)	NOT NULL,
		CloseChar		char(1)	NOT NULL,
		SyntaxPoints	int		NOT NULL,
		ClosePoints		int		NOT NULL,
	);

	INSERT INTO #map (OpenChar, CloseChar, SyntaxPoints, ClosePoints)
	VALUES ('(',')',3,1), ('[',']',57,2), ('{','}',1197,3), ('<','>',25137,4)
