	-- Mark corrupted strings
	IF OBJECT_ID('tempdb..#corruptedStrings','U') IS NOT NULL DROP TABLE #corruptedStrings; --SELECT * FROM #corruptedStrings
	SELECT x.RowID, x.SyntaxPoints
	INTO #corruptedStrings
	FROM (
		SELECT x.RowID, m.SyntaxPoints
			, rn = ROW_NUMBER() OVER (PARTITION BY x.RowID ORDER BY x.ID)
		FROM #data x
			JOIN #map m ON m.CloseChar = x.Val
		WHERE m.OpenChar <> x.MatchOpenSide
	) x
	WHERE rn = 1

	UPDATE d SET d.IsCorrupted = 1
	FROM #data d
		JOIN #corruptedStrings cs ON cs.RowID = d.RowID

	SELECT Answer = SUM(c.SyntaxPoints)
	FROM #corruptedStrings c
