------------------------------------------------------------------------------
	SELECT DISTINCT Answer = PERCENTILE_DISC(0.5) WITHIN GROUP (ORDER BY SUM(POWER(CONVERT(bigint, 5), d.BracketLevel-1) * m.ClosePoints)) OVER ()
	FROM #data d
		JOIN #map m ON m.OpenChar = d.Val
	WHERE d.MatchCloseSide IS NULL
		AND d.IsCorrupted = 0
	GROUP BY d.RowID
